import React, { useState } from 'react';

export default function Rows() {
  const [color, setColor] = useState('');

  const handleMouseDown = (e) => {
    e.target.style.backgroundColor = '#efefef5c';
  };
  const handleMouseUp = (e) => {
    e.target.style.backgroundColor = '';
  };

  const handleOnMouseOver = (e) => {
    e.target.style.backgroundColor = '#efefef1c';
  };
  const handleOnMouseOut = (e) => {
    e.target.style.backgroundColor = '';
  };
  const printSquares = (quantity) => {
    for (let i = 0; i < quantity; i++) {
      console.log('yep');
    }
  };
  return (
    <>
      <div className='b-row first-row'>
        {printSquares(2)}
        <div
          className='square'
          data-index='0'
          onMouseOver={handleOnMouseOver}
          onMouseLeave={handleOnMouseOut}
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
        <div
          className='square'
          data-index='1'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
        <div
          className='square'
          data-index='2'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
      </div>
      <div className='b-row second-row'>
        <div
          className='square'
          data-index='3'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
        <div
          className='square'
          data-index='4'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
        <div
          className='square'
          data-index='5'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
      </div>
      <div className='b-row third-row'>
        <div
          className='square'
          data-index='6'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
        <div
          className='square'
          data-index='7'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
        <div
          className='square'
          data-index='8'
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        ></div>
      </div>
    </>
  );
}
