import React, { useEffect, useState } from 'react';

export default function Rows() {
  const [enter, mouseEnter] = useState(false);
  const bgCol = {
    backgroundColor: enter ? '#efefef1c' : 'none',
  };

  //   const cssHover = {
  //     backgroundColor: '#efefef1c',
  //   };
  useEffect(() => {
    setBgColor({ backgroundColor: enter ? '#efefef1c' : 'none' });
  }, [enter]);
  return (
    <>
      <div className='b-row first-row'>
        <div
          className='square'
          data-index='0'
          onMouseOver={() => {
            mouseEnter(!enter);
          }}
          onMouseOut={() => {
            mouseEnter(!enter);
          }}
          style={bgCol}
        ></div>
        <div className='square' data-index='1'></div>
        <div className='square' data-index='2'></div>
      </div>
      <div className='b-row second-row'>
        <div className='square' data-index='3'></div>
        <div className='square' data-index='4'></div>
        <div className='square' data-index='5'></div>
      </div>
      <div className='b-row third-row'>
        <div className='square' data-index='6'></div>
        <div className='square' data-index='7'></div>
        <div className='square' data-index='8'></div>
      </div>
    </>
  );
}
