import React, { useState } from 'react';
import Rows from './Rows';
// import Squares from './Squares';

export default function Board() {
  const [board, setBoard] = useState(Array(9).fill(null));
  //   const [boardFull, setBoardBool] = useState(false);
  const [game, setGame] = useState(false);
  const initialJSX = (
    <>
      <h1>Tic-Tac-Toe</h1>
      <div className='board'>
        <Rows fillSquare={fillSquare} board={board} game={game} />
      </div>
    </>
  );
  const [JSX, setJSX] = useState(initialJSX);

  let round = 0;

  function fillSquare(sqId) {
    const player = round % 2 === 0 ? 'X' : 'O';
    board[sqId] = player;
    // console.log(`Ronda: ${round}, jugador ${player}, ha colocado en ${sqId}`);
    round++;
    setBoard(board);
    const solutions = [
      [0, 1, 2],
      [0, 4, 8],
      [0, 3, 6],
      [1, 4, 7],
      [3, 4, 5],
      [6, 7, 8],
      [2, 5, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < solutions.length; i++) {
      const [a, b, c] = solutions[i];
      if (board[a] && board[a] === board[b] && board[b] === board[c]) {
        const Winner = board[a];
        setGame(true);
        console.log(game);
        const newJSX = (
          <>
            <h1>Tic-Tac-Toe</h1>
            <div className='endGame'>
              <h1>Ha ganado {Winner}</h1>
            </div>
            <Rows
              fillSquare={fillSquare}
              board={board}
              game={game}
              winners={solutions[i]}
            />
          </>
        );
        return setJSX(newJSX);
      }
    }
    if (!board.includes(null)) {
      const newJSX = (
        <>
          <h1>Tic-Tac-Toe</h1>
          <div className='endGame'>
            <h1>It was a tie!</h1>
          </div>
          <Rows fillSquare={fillSquare} board={board} />
        </>
      );
      return setJSX(newJSX);
    }
  }

  // useEffect(() => {
  //   console.log('hola soy el useEffect y estoy siendo ejecutado');
  // }, [boardFull]);

  //   useEffect(() => {}, [winner]);
  return JSX;
}
